import * as yup from "yup";
export const contactSchema = yup.object().shape({
  fullname: yup.string().required("نام و نام خانوادگی الزامی می یاشد!"),
  photo: yup.string().url("آدرس ایمیل معتبر نیست!").required("آدرس تصویر الزامی میباشد!"),
  mobile: yup.number().required("شماره تماس الزامی می باشد!"),
  email: yup.string().email("آدرس ایمیل معتبر نمی باشد!").required("آدرس ایمیل الزامی می باشد!"),
  job: yup.string().nullable(),
  group: yup.string().required("انتخاب گروه الزامی می باشد")
});
